INSERT INTO `proevent_content` VALUES ('51', '80', 'Корпоративные проекты', 'corporate_projects', '', '<p>- День рождения компании</p>\r\n<p>- Календарные праздники</p>\r\n<p>- Teambuilding</p>\r\n<p> </p>\r\n<p style=\"text-align: justify;\">В город приходит жизнь и появляются деньги, когда по утренним, оживающим от таинства сна улицам, наперегонки с грачами и журавлями несутся вездесущие рыцари капитализма – менеджеры среднего звена. Ах, менеджеры, менеджеры, возможно, вы считаете их простой функцией в тригонометрии управления, но ведь каждый из них является частью огромного рабочего механизма, способного продать горы товаров, продвинуть тысячи  услуг и добиться заветной цели своего руководителя по завоеванию рынка. И если этому трудяге не подарить вовремя настоящий Праздник, или вместо этого дать ему задание этот самый Праздник организовать, механизм может дать сбой.<br />Праздник это не только серьезное дело, праздник – это искусство. А в мире искусства самое важное опыт, мнение и помощь квалифицированного эксперта, дабы вам не подсунули вместо пасхального яйца от Фаберже, пардон мон шер ами, самобытное яйцо доморощенного художника Воронки. Эксперт экономит время, деньги и самое главное – нервы - главный гарант здоровых взаимоотношений в коллективе. Хорошо, что не надо далеко ходить, ведь мы рядом…</p>\r\n<p> </p>\r\n<p>{gallery}projects/corporate/images{/gallery}</p>', '', '1', '0', '0', '9', '2012-07-19 10:37:14', '42', '', '2012-07-19 11:08:36', '42', '0', '0000-00-00 00:00:00', '2012-07-19 10:37:14', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_